import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [captchaInput, setCaptchaInput] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!role || !username || !password || !captchaInput) {
      alert('Please fill all fields.');
      return;
    }
    if (role === 'Admin') {
      alert('Redirecting to Admin credential creation...');
      // Implement admin registration logic here
    } else {
      navigate('/home');
    }
  };

  return (
    <div className="login-page">
      <select onChange={(e) => setRole(e.target.value)} value={role}>
        <option value="">Default select</option>
        <option value="Admin">Admin</option>
        <option value="User">User</option>
      </select>
      <input type="text" placeholder="User Name" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <input type="text" placeholder="Enter Captcha" value={captchaInput} onChange={(e) => setCaptchaInput(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
}
